import React, { useState } from 'react'
import { CContainer, CForm, CFormInput, CFormLabel, CFormText, CFormCheck, CButton } from '@coreui/react'
import axios from 'axios'


function AdminLogin() {

    const [loginCred, setLoginCred] = useState({})

    const setCred = (e,field) => {
        setLoginCred({...loginCred, [field] : e.target.value})
    }

    const setTheLang = (e) => {
        setLoginCred({...loginCred, lang : e.target.value})
        sessionStorage.setItem('lang',e.target.value)
    }
    const [headText, setHeadText] = useState("ADMIN LOGIN");
    var settings = {
        params : {
            'api-version':'3.0',
            'to': 'yo'
          },
        headers: {
            "Ocp-Apim-Subscription-Key" : `${process.env.REACT_APP_TRANS_API}`,
            "Ocp-Apim-Subscription-Region" : "westeurope",
        }
    };

    const requestBody = [
        {
          text: headText
        }
      ];

    const handleTranslate = async (e) =>{
        e.preventDefault()
        axios
          .post("https://api.cognitive.microsofttranslator.com/translate",requestBody, settings)
          .then((res) => {
            setHeadText(res.data[0].translations[0].text)
          })
          .catch((error) => {
            console.log(error);
          });
      }


    const Login = (e) => {
        e.preventDefault()
        if(loginCred.email != 'admin@gmail.com' || loginCred.password != '12345678'){
            alert('Invalid credentials')
            return
        }else{
            if(loginCred.lang!='en'){

            }
            window.location.href='/admin/reported'
            //call endpoint
        }
    }
  return (
    <>
    <div className='' style={{fontSize:"30px"}}><b>{headText}</b></div>
    <CContainer className='border rounded bg-white mt-5 p-3'>
        <CForm className='text-start' onSubmit={Login}>
            <div className="mb-3">
                <CFormLabel htmlFor="exampleInputEmail1">Email address</CFormLabel>
                <CFormInput onChange={(e) => setCred(e,'email')} type="email" id="exampleInputEmail1" aria-describedby="emailHelp" />
            </div>
            <div className="mb-3">
                <CFormLabel htmlFor="exampleInputPassword1">Email Password</CFormLabel>
                <CFormInput onChange={(e) => setCred(e,'password')} type="password" id="exampleInputPassword1" />
            </div>
            <div className="mb-3">
                <select onChange={(e) => setTheLang(e)} className="form-select" aria-label="Default select example">
                    <option value="en">English</option>
                    <option value="ig">Igbo</option>
                    <option value="yo">Yoruba</option>
                    <option value="ha">Hausa</option>
                </select>
            </div>
            <CButton type="submit" color="primary" style={{background:"#184B8D"}}>
               Login
            </CButton>
        </CForm>
    </CContainer>
    </>
  )
}

export default AdminLogin
