import { CTable, CTableBody, CTableDataCell, CTableHead, CTableHeaderCell, CTableRow } from '@coreui/react'
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'

function ReportedIncidents() {

    const reports = [
        {
            id:1,
            title : 'Errorrr',
            severity: 'High',
            Date : '22-10-2023',
            status : 'Processing'
        },
      ]
    let i =1;
    const viewOne = () => {
        window.location.href='/admin/single'
    }
    //conversions
    let [dashTitle, setDashTitle] = useState('Admin Dashboard')
    let [link1, setLink1] = useState('Reported Incidents')
    let [logText, setLogText] = useState('Logout')
    let [titleText, setTitleText] = useState('Title')
    let [severeText, setSevereText] = useState('Severity')
    let [statusText, setStatusText] = useState('Status')
    let [actionText, setActionText] = useState('Action')
    let [dateText, setDateText] = useState('Date')
    let [viewText, setViewText] = useState('View More')

    const checkTranslation = () => {
        if(sessionStorage.getItem('lang') != 'en'){
            setDashTitle(handleTranslation(dashTitle))
            // console.log(handleTranslation(dashTitle))
            // setLink1(handleTranslation(link1))
            // setLogText(handleTranslation(logText))
            // setTitleText(handleTranslation(titleText))
            // setSevereText(handleTranslation(severeText))
            // setStatusText(handleTranslation(statusText))
            // setActionText(handleTranslation(actionText))
            // setDateText(handleTranslation(dateText))
            // setViewText(handleTranslation(viewText))

        }
    } 

    useEffect(() => {
        // checkTranslation()
    },[])

    var settings = {
        params : {
            'api-version':'3.0',
            'to': sessionStorage.getItem('lang')
          },
        headers: {
            "Ocp-Apim-Subscription-Key" : `${process.env.REACT_APP_TRANS_API}`,
            "Ocp-Apim-Subscription-Region" : "westeurope",
        }
    };


    const handleTranslation = (text) => {
        const requestBody = [
            {
              text: text
            }
          ];
        axios
          .post("https://api.cognitive.microsofttranslator.com/translate",requestBody, settings)
          .then((res) => {
            console.log(res.data[0].translations[0].text)
            return res.data[0].translations[0].text
          })
          .catch((error) => {
            console.log(error);
          });
    }

  return (
    <div class="container text-center">
    <div class="row">
        <div class="col-12 col-lg-3 bg-white mr-2 border rounded text-start py-3 hidden">
            <ul style={{listStyle:"none"}}>
                <li><b>{dashTitle}</b></li>
                <li><Link to="/admin/reported" style={{textDecoration:"none"}}>{link1}</Link></li>
                <li><Link to="/admin" style={{textDecoration:"none"}}>{logText}</Link></li>
                <li className='mt-5 w-50'>
                    <select class="form-select" >
                        <option value="en">English</option>
                        <option value="ig">Igbo</option>
                        <option value="yo">Yoruba</option>
                        <option value="ha">Hausa</option>
                    </select>
                </li>
            </ul>
        </div>
        <div class="col-1">
        </div>
        <div class="col-12 col-lg-8 bg-white border rounded py-3 text-start">
            <div><b>Reported Incidents</b></div>
            <div className='mt-3'>
            <CTable>
                <CTableHead>
                    <CTableRow>
                        <CTableHeaderCell scope="col">S/n</CTableHeaderCell>
                        <CTableHeaderCell scope="col">{titleText}</CTableHeaderCell>
                        <CTableHeaderCell scope="col">{severeText}</CTableHeaderCell>
                        <CTableHeaderCell scope="col">{dateText}</CTableHeaderCell>
                        <CTableHeaderCell scope="col">{statusText}</CTableHeaderCell>
                        <CTableHeaderCell scope="col">{actionText}</CTableHeaderCell>
                    </CTableRow>
                </CTableHead>
                <CTableBody>
                    {reports.map(({id,title,severity,Date,status}) => {
                        return (
                            <CTableRow key={id}>
                                <CTableHeaderCell scope="row">{i++}</CTableHeaderCell>
                                <CTableDataCell>{title}</CTableDataCell>
                                <CTableDataCell>{severity}</CTableDataCell>
                                <CTableDataCell>{Date}</CTableDataCell>
                                <CTableDataCell>{status}</CTableDataCell>
                                <CTableDataCell onClick={() => viewOne(id)}><button className='bg-primary text-white rounded outline-none'>{viewText}</button></CTableDataCell>
                            </CTableRow>
                        )
                    })}
                </CTableBody>
                </CTable>
            </div>
        </div>
    </div>
    </div>
  )
}

export default ReportedIncidents
